// Generated by CoffeeScript 1.6.3
KISSY.add('gallery-js', function(S, ComboBox) {
  var comboBox, data, tmpl;
  tmpl = "<a href='http://gallery.kissyui.com/{name}/{version}/guide/index.html'><div class='item-wrapper'>" + "{name}" + "<span> by {userName}</span>" + "</div></a>";
  data = new ComboBox.RemoteDataSource({
    xhrCfg: {
      url: './api/search',
      dataType: 'jsonp'
    },
    paramName: "name",
    parse: function(query, results) {
      return results.result;
    },
    cache: true
  });
  comboBox = new ComboBox({
    prefixCls: 'search-',
    placeholder: '点我搜索',
    srcNode: S.one("#combobox"),
    dataSource: data,
    format: function(query, results) {
      var ret;
      ret = [];
      S.each(results, function(r) {
        var item;
        r.userName = r.author.name;
        item = {
          textContent: r.name,
          content: S.substitute(tmpl, r)
        };
        return ret.push(item);
      });
      return ret;
    }
  });
  return comboBox.render();
}, {
  requires: ['combobox']
});
