$(function(){var a=0;$(".github-widget").each(function(){if(a==0){$("head").append('<style type="text/css">.github-box{font-family:helvetica,arial,sans-serif;font-size:13px;line-height:18px;background:#fafafa;border:1px solid #ddd;color:#666;border-radius:3px}.github-box a{color:#4183c4;border:0;text-decoration:none}.github-box .github-box-title{position:relative;border-bottom:1px solid #ddd;border-radius:3px 3px 0 0;background:#fcfcfc;background:-moz-linear-gradient(#fcfcfc,#ebebeb);background:-webkit-linear-gradient(#fcfcfc,#ebebeb);}.github-box .github-box-title h3{font-family:helvetica,arial,sans-serif;font-weight:normal;font-size:16px;color:gray;margin:0;padding:10px 10px 10px 80px;background:url(http://www.oschina.net/img/github_logo.gif) center left no-repeat}.github-box .github-box-title h3 .repo{font-weight:bold}.github-box .github-box-title .github-stats{position:absolute;top:8px;right:10px;background:white;border:1px solid #ddd;border-radius:3px;font-size:11px;font-weight:bold;line-height:21px;height:21px;padding-left:2px;}.github-box .github-box-title .github-stats a{display:inline-block;height:21px;color:#666;padding:0 5px 0 5px;}.github-box .github-box-title .github-stats .watchers{border-right:1px solid #ddd;background-position:3px -2px;}.github-box .github-box-title .github-stats .forks{background-position:0 -52px;padding-left:5px}.github-box .github-box-content{padding:10px;font-weight:300}.github-box .github-box-content p{margin:0}.github-box .github-box-content .link{font-weight:bold}.github-box .github-box-download{position:relative;border-top:1px solid #ddd;background:white;border-radius:0 0 3px 3px;padding:10px;height:24px}.github-box .github-box-download .updated{margin:0;font-size:11px;color:#666;line-height:24px;font-weight:300}.github-box .github-box-download .updated strong{font-weight:bold;color:#000}.github-box .github-box-download .download{position:absolute;display:block;top:10px;right:10px;height:24px;line-height:24px;font-size:12px;color:#666;font-weight:bold;text-shadow:0 1px 0 rgba(255,255,255,0.9);padding:0 10px;border:1px solid #ddd;border-bottom-color:#bbb;border-radius:3px;background:#f5f5f5;background:-moz-linear-gradient(#f5f5f5,#e5e5e5);background:-webkit-linear-gradient(#f5f5f5,#e5e5e5);}.github-box .github-box-download .download:hover{color:#527894;border-color:#cfe3ed;border-bottom-color:#9fc7db;background:#f1f7fa;background:-moz-linear-gradient(#f1f7fa,#dbeaf1);background:-webkit-linear-gradient(#f1f7fa,#dbeaf1);</style>')}a++;var c=$(this);var b=c.data("repo");$.ajax({url:"https://api.github.com/repos/"+b,dataType:"jsonp",success:function(e){var d=e.data;var f=d.parent?d.parent:d;var g=d.pushed_at.substr(0,10);if(d.homepage==null||d.homepage=="null"){d.homepage=""}var h=$(' 					<div class="github-box repo">  					    <div class="github-box-title"> 					        <h3> 					            <a class="owner" href="'+f.owner.url.replace("api.","").replace("users/","")+'" target="_blank">'+f.owner.login+'</a> 					            /  					            <a class="repo" href="'+f.url.replace("api.","").replace("repos/","")+'" target="_blank">'+f.name+'</a> 					        </h3> 					        <div class="github-stats"> 					        Watch<a class="watchers" title="Watchers" href="'+f.url.replace("api.","").replace("repos/","")+'/watchers" target="_blank">'+f.watchers+'</a> 					        Fork<a class="forks" title="Forks" href="'+f.url.replace("api.","").replace("repos/","")+'/network" target="_blank">'+f.forks+'</a> 					        </div> 					    </div> 					    <div class="github-box-content"> 					        <p class="description">'+f.description+' &mdash; <a href="'+f.url.replace("api.","").replace("repos/","")+'#readme"  target="_blank">More...</a></p> 					        <table class="issues" width="100%"></table> 					    </div> 					    <div class="github-box-download"> 					        <p class="updated"><a href="'+f.url.replace("api.","").replace("repos/","")+'/tree/master" target="_blank"><strong>master</strong>分支</a>代码最近更新：'+g+'</p> 					        <a class="btn btn-priamry" target="_blank" href="'+f.url.replace("api.","").replace("repos/","")+'/issues/new">提交issues</a>                             <a class="download" href="'+f.url.replace("api.","").replace("repos/","")+'/zipball/master">下载zip</a> 					    </div> 					</div> 				');h.appendTo(c);if(f.has_issues&&f.open_issues>0){$.ajax({url:"https://api.github.com/repos/"+f.owner.login+"/"+f.name+"/issues",dataType:"jsonp",data:{state:"open",per_page:5,page:1,sort:"update"},success:function(m){var k=m.data;var n=$(".github-box-content table");for(var l=0;l<k.length;l++){var j=k[l].updated_at.substr(0,10);n.append('<tr> 										<td class="number" width="30">#'+k[l].number+'</td> 										<td class="info"><a href="'+k[l].html_url+'" target="_blank">'+k[l].title+'</a></td> 										<td class="author" width="200" align="right">by <a href="'+k[l].user.url.replace("api.","").replace("users/","")+'" target="_blank">'+k[l].user.login+'</a>&nbsp;&nbsp;<em style="font-size: 8pt;font-family: Candara,arial;cursor:default;color: #fff;-webkit-text-size-adjust: none;">'+j+"</em></td></tr> 								")}}})}}})})});
